	('UID', 101, NULL, NULL, 'IANA reserved, no RFC documented them [2] and support was removed from BIND in the early 90s: UINFO(100), UID(101), GID(102), UNSPEC(103)', FALSE, TRUE, NULL),
	('GID', 102, NULL, NULL, 'IANA reserved, no RFC documented them [2] and support was removed from BIND in the early 90s: UINFO(100), UID(101), GID(102), UNSPEC(103)', FALSE, TRUE, NULL),
