-- https://en.wikipedia.org/wiki/List_of_DNS_record_types
-- https://www.iana.org/assignments/dns-parameters/dns-parameters.xhtml
CREATE TABLE IF NOT EXISTS DomainNameSystemResourceRecordTypes
(
    name            VARCHAR(16)         NOT NULL PRIMARY KEY,
    typeNumber      SMALLINT UNSIGNED   NOT NULL, -- Cannot be UNIQUE, because newer RR types have inherited obsoleted numbers.
    definingRFC     SMALLINT UNSIGNED   NULL,
    nameMeaning     TINYTEXT            NULL,
    description     MEDIUMTEXT          NULL,
    pseudoRecord    BOOLEAN             NULL,
    obsolete        BOOLEAN             NULL,
    obsoletingRFC   SMALLINT UNSIGNED   NULL
);

;